const initialState = {
    open: false,
    modalIsOpen: false
}

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'ADD_PROJECT':
            return {
                ...state,
            }
        case 'HANDLE_TOGGLE':
            return {
                ...state,
                open: !state.open
            }
        case 'OPEN_MODAL':
            return {
                ...state,
                modalIsOpen: true
            }
        case 'OPEN_MODAL':
            return {
                ...state,
                modalIsOpen: true
            }
        //    
        default:
            return state;
    }
}

export default rootReducer;
