import React, { Component } from 'react';
import styled from 'styled-components';
//Redux
import { connect } from 'react-redux';
import { closeModal, addProject } from "../../../redux/actions/portfolioActions";

class Modal extends Component {

    state = {
        title: '',
        employer: '',
        image: '',
        linke: ''
    }

    onChangeHandler = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
        console.log(this.state)
    }



    render() {
        const Title = styled.h1`
        color: #ffffff;
        font-size: 30px;
        text-align: center;
        `;

        return (
            <>
                <Title>Add new Portfolio!</Title>
                <input onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Project name' name='title' /> <br />
                <input onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Company' name='employer' /> <br />
                <input onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Link to the image' name='image' /> <br />
                <input onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Link to the project' name='link' /> <br />
                <button onClick={() => this.props.addProject()}>Add</button>
                <button onClick={() => this.props.closeModal()}>close</button>
            </>
        )
    }

}

const mapDispatchToProps = {
    closeModal,
    addProject
}

export default connect(null, mapDispatchToProps)(Modal);
