import React from 'react';
import { BarChart, Bar,ResponsiveContainer, XAxis, YAxis, Tooltip} from 'recharts';

const SkillCharts = () => {

    const DataFormater = (number) => {
        if(number === 1){
            return 'Basics';
        }else if(number === 2){
            return 'Some experience';
        }else if(number === 3){
            return 'Well experienced';
        }else if(number === 4){
            return 'Fluent';
        }else return '';
      }

    const data = [
        {
          name: 'React', Skills: 2,
        },
        {
          name: 'JavaScript', Skills: 3,
        },
        {
          name: 'CSS', Skills: 4,
        },
        {
          name: 'HTML', Skills: 4,
        },
        {
          name: 'PHP', Skills: 1,
        },
      ];

    return(
        <ResponsiveContainer>
            <BarChart data={data}>
                <XAxis dataKey="name" />
                <YAxis tickFormatter={DataFormater} /> 
                <Tooltip />
                <Bar dataKey="Skills" fill="#8884d8" animationDuration={1700} />
            </BarChart>
        </ResponsiveContainer>
    );
}

export default SkillCharts;
