import React from 'react';
import { BarChart, Bar,ResponsiveContainer, XAxis, YAxis, Tooltip, Label} from 'recharts';

const SkillCharts = () => {

    const DataFormater = (number) => {
        if(number > 1000000000){
          return (number/1000000000).toString() + 'B';
        }else if(number > 1000000){
          return (number/1000000).toString() + 'M';
        }else if(number > 1000){
          return (number/1000).toString() + 'K';
        }else{
          return number.toString();
        }
      }

    const data = [
        {
          name: 'React', Skills: 3,
        },
        {
          name: 'JavaScript', Skills: 3,
        },
        {
          name: 'CSS', Skills: 4,
        },
        {
          name: 'HTML', Skills: 4,
        },
        {
          name: 'PHP', Skills: 2,
        },
      ];

    return(
        <ResponsiveContainer>
            <BarChart data={data}>
                <XAxis dataKey="name" />
                <YAxis> 

                </YAxis>
                <Tooltip />
                <Bar dataKey="Skills" fill="#8884d8" />
            </BarChart>
        </ResponsiveContainer>
    );
}

export default SkillCharts;
