import React, { Component } from 'react';
import styled from 'styled-components';
//Redux
import { connect } from 'react-redux';
import { closeModal, addProject } from "../../../redux/actions/portfolioActions";

class Modal extends Component {

    render() {

        const Title = styled.h1`
        color: #ffffff;
        font-size: 30px;
        text-align: center;
        `;

        return (
            <>
                <Title>Add new Portfolio!</Title>
                <input type='text' placeholder='Project name' name='title' /> <br />
                <input type='text' placeholder='Company' name='employer' /> <br />
                <input type='text' placeholder='Link to the image' name='image' /> <br />
                <input type='text' placeholder='Link to the project' name='link' /> <br />
                <button onClick={() => this.props.addProject()}>Add</button>
                <button onClick={() => this.props.closeModal()}>close</button>
            </>
        )
    }

}

const mapDispatchToProps = {
    closeModal,
    addProject
}

export default connect(null, mapDispatchToProps)(Modal);
