import React from 'react';
import styled from 'styled-components';
//Redux
import { connect } from 'react-redux';
import { closeModal } from "../../../redux/actions/portfolioActions";

const Modal = (props) => {

    const Title = styled.h1`
        color: #ffffff;
        font-size: 30px;
        text-align: center;
    `;

    return (
        <>
            <Title>Add new Portfolio!</Title>
            <input type='text' placeholder='Project name' name='title' />
            <input type='text' placeholder='Company' name='employer' />
            <input type='text' placeholder='Link to the image' name='image' />
            <input type='text' placeholder='Link to the project' name='link' />
            <button onClick={() => props.closeModal()}>close</button>
        </>
    )
}

const mapDispatchToProps = {
    closeModal
}

export default connect(mapStateToProps, mapDispatchToProps)(Modal);
