const initialState = {
    open: false,
    modalIsOpen: false,
    portfolioData: [
        {
            title: 'Bactim',
            employer: '3Step',
            image: 'https://bactim.pl/public/tpl/bactim/img/zdj1.jpg',
            link: 'https://bactim.pl'
        },
        {
            title: 'Wonat',
            employer: '3Step',
            image: 'http://wonat.pl/public/tpl/panel/img/img-big-2.png',
            link: 'https://wonat.pl'
        },
        {
            title: 'Fimber',
            employer: 'Fimber Technologies',
            image: 'https://www.gannett-cdn.com/-mm-/724f2a7cbc0c3a4055ef18e10952159ddef9c3bd/c=216-514-2193-2000/local/-/media/2015/08/13/USATODAY/USATODAY/635750314502188185-DG016-087CLtg01ev8kkr9se32fehieqe07fr.jpg?width=520&height=390&fit=crop',
            link: 'http://www.fimber.no'
        },
        {
            title: 'Bimm (Car)',
            employer: 'Bimm',
            image: 'https://tenerifesportscars.com/wp-content/uploads/car-rental-gallery/1485207906_Ford-Mustang-Yellow-1.jpg',
            link: 'https://www.bimm.co/'
        },
        {
            title: 'Bimm (Scooter)',
            employer: 'Bimm',
            image: 'https://thumbor.forbes.com/thumbor/960x0/https%3A%2F%2Fspecials-images.forbesimg.com%2Fdam%2Fimageserve%2F1125854934%2F960x0.jpg%3Ffit%3Dscale',
            link: 'https://www.bimm.co/'
        },
    ]
}

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'ADD_PROJECT':
            return {
                ...state,
            }
        case 'HANDLE_TOGGLE':
            return {
                ...state,
                open: !state.open
            }
        case 'OPEN_MODAL':
            return {
                ...state,
                modalIsOpen: true
            }
        case 'CLOSE_MODAL':
            return {
                ...state,
                modalIsOpen: false
            }
        //    
        default:
            return state;
    }
}

export default rootReducer;
