import React, { Component } from 'react';
import styled from 'styled-components';

import Home from './components/Home/Home';
import About from './components/About/About';
import MySkills from './components/MySkills/MySkills';
import Experience from './components/Experience/Experience';
import Portfolio from './components/Portfolio/Portfolio';

import { Route, BrowserRouter, NavLink } from 'react-router-dom';
import Drawer from '@material-ui/core/Drawer';

class App extends Component {
  state = {
    "open": false
  };

  handleToggle = () => this.setState({ open: !this.state.open });

  render() {

    const StyledLink = styled(NavLink)`
      text-decoration: none;
      color: green;
    `;

    const NavButton = styled.div`
      transition: all 0.3s;
      position: fixed;
      left: 20px;
      top: 20px;
      width: 50px;
      height: 50px;
      outline: none;
      border: none;
      background-color: rgba(255,255,255,0.5);
      :after{
        content: '';
        position: absolute;
        width: 40px;
        left: 5px;
        height: 2px;
        background-color: #2ECC71;
      }
    `;

    const NavLine = styled.div`
      transition: all 0.3s;
      height: 2px;
      position: absolute;
      background-color: ${this.state.open ? 'transparent' : '#2ECC71'} ;
      width: 40px;
      top: calc(50% - 1px);
      left: 5px;
    `;

    return (
      <BrowserRouter>

        <Drawer open={this.state.open} >


          <StyledLink onClick={this.handleToggle} to='/'>Home</StyledLink>
          <StyledLink onClick={this.handleToggle} to='/About'>About</StyledLink>

          <button onClick={this.handleToggle}>
            <div></div>
          </button>
        </Drawer>

        <Route path='/' exact component={Home} />
        <Route path='/About' component={About} />
        <Route path='/MySkills' component={MySkills} />
        <Route path='/Experience' component={Experience} />
        <Route path='/Portfolio' component={Portfolio} />

        <NavButton onClick={this.handleToggle}>
          <NavLine />
        </NavButton>
      </BrowserRouter>
    );
  }
}

export default App;
