import React, { Component } from 'react';
import styled, {ThemeProvider} from 'styled-components';
import theme from './globals/theme';

import Home from './components/Home/Home';
import About from './components/About/About';
import MySkills from './components/MySkills/MySkills';
import Experience from './components/Experience/Experience';
import Portfolio from './components/Portfolio/Portfolio';

import { Route, BrowserRouter, NavLink } from 'react-router-dom';
import Drawer from '@material-ui/core/Drawer';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';

class App extends Component {
  state = {
    "open": false,
  };

  handleToggle = () => this.setState({ open: !this.state.open });

  render() {

    const StyledLink = styled(NavLink)`
      transition: 0.3s;
      text-decoration: none;
      color: green;
      width: 100px;
      text-align: center;
      padding: 10px 5px;
      margin: 10px 5px;
      border: 3px solid #cccccc;
      :hover{
        border-color: #2ECC71;
      }
      :active{
        border-color: #2ECC71;
      }
    `;

    const NavButton = styled.div`
      transition: all 0.3s;
      cursor: pointer;
      position: fixed;
      left: 20px;
      top: 20px;
      width: 50px;
      height: 50px;
      outline: none;
      border: none;
      background-color: rgba(255,255,255,0.5);
      :after{
        content: '';
        position: absolute;
        width: 40px;
        left: 5px;
        top: ${this.state.open ? '25' : '10'}px;
        height: 2px;
        background-color: #2ECC71;
        transform: rotate(${this.state.open ? '-45' : '0'}deg); 
      }
      :before{
        transition: 0.3s;
        content: '';
        position: absolute;
        width: 40px;
        left: 5px;
        bottom: ${this.state.open ? '23' : '10'}px;
        height: 2px;
        background-color: #2ECC71;
        transform: rotate(${this.state.open ? '45' : '0'}deg); 
      }
    `;

    const NavLine = styled.div`
      transition: all 0.3s;
      height: 2px;
      position: absolute;
      background-color: #2ECC71;
      width: 40px;
      top: calc(50% - 1px);
      left: 5px;
      opacity: ${this.state.open ? '0' : '1'};
    `;

    const CloseBtn = styled.p`
      position: absolute;
      bottom: 20px;
      transition: 0.3s;
      text-decoration: none;
      color: green;
      width: 100px;
      text-align: center;
      padding: 10px 5px;
      margin: 10px 5px;
      border: 3px solid #cccccc;
      :hover{
        border-color: #2ECC71;
        cursor: pointer;
      }
    `;      

    return (
      <ThemeProvider theme={theme}>
        <BrowserRouter>

        <ClickAwayListener onClickAway={this.state.open = false}>
          <Drawer open={this.state.open} >


            <StyledLink onClick={this.handleToggle} exact to='/' activeStyle={{borderColor: '#2ECC71'}}>Home</StyledLink>
            <StyledLink onClick={this.handleToggle} exact to='/About' activeStyle={{borderColor: '#2ECC71'}}>About</StyledLink>
            <StyledLink onClick={this.handleToggle} exact to='/MySkills' activeStyle={{borderColor: '#2ECC71'}}>My Skills</StyledLink>
            <StyledLink onClick={this.handleToggle} exact to='/Experience' activeStyle={{borderColor: '#2ECC71'}}>Experience</StyledLink>
            <StyledLink onClick={this.handleToggle} exact to='/Portfolio' activeStyle={{borderColor: '#2ECC71'}}>Portfolio</StyledLink>

            <CloseBtn onClick={this.handleToggle}>Close</CloseBtn>
          </Drawer>
        </ClickAwayListener>

          <Route path='/' exact component={Home} />
          <Route path='/About' component={About} />
          <Route path='/MySkills' component={MySkills} />
          <Route path='/Experience' component={Experience} />
          <Route path='/Portfolio' component={Portfolio} />

          <NavButton onClick={this.handleToggle}>
            <NavLine />
          </NavButton>
        </BrowserRouter>
      </ThemeProvider>
    );
  }
}

export default App;
