import React, { Component } from 'react';
import styled from 'styled-components';
//Redux
import { connect } from 'react-redux';
import { closeModal, addProject } from "../../../redux/actions/portfolioActions";

class Modal extends Component {

    state = {
        title: '',
        employer: '',
        image: '',
        link: ''
    }

    onChangeHandler = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })

        console.log(this.state);
    }

    render() {
        const Title = styled.h1`
        color: #ffffff;
        font-size: 30px;
        text-align: center;
        `;

        const InputWrapper = styled.div`
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-content: flex-start;
            text-align: center;
        `;

        const StyledInput = styled.input`
            border: none;
            outline: none;
            height: 30px;
            padding: 0px 5px;
            margin: 2px;
            :focus{

            }
        `;

        return (
            <>
                <Title>Add new Portfolio!</Title>
                <InputWrapper>
                    <StyledInput onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Project name' name='title' />
                    <StyledInput onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Company' name='employer' />
                    <StyledInput onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Link to the image' name='image' />
                    <StyledInput onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Link to the project' name='link' />
                </InputWrapper>

                <button onClick={() => this.props.addProject(this.state)}>Add</button>
                <button onClick={() => this.props.closeModal()}>close</button>
            </>
        )
    }

}

const mapDispatchToProps = {
    closeModal,
    addProject
}

export default connect(null, mapDispatchToProps)(Modal);
