import React, { Component } from 'react';
import styled from 'styled-components';
//Redux
import { connect } from 'react-redux';
import { closeModal, addProject } from "../../../redux/actions/portfolioActions";
import { NONAME } from 'dns';

class Modal extends Component {

    state = {
        title: '',
        employer: '',
        image: '',
        link: ''
    }

    onChangeHandler = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })

        console.log(this.state);
    }



    render() {
        const Title = styled.h1`
        color: #ffffff;
        font-size: 30px;
        text-align: center;
        `;

        const inputStyle = { //styled component makes it doesn't work
            "& input": { // set it to parent component so no need to give every input class
                background: "red",
                border: 'none',
            }
        }

        return (
            <>
                <Title>Add new Portfolio!</Title>
                <input onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Project name' name='title' />
                <input onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Company' name='employer' />
                <input onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Link to the image' name='image' />
                <input onChange={(e) => this.onChangeHandler(e)} type='text' placeholder='Link to the project' name='link' />
                <button onClick={() => this.props.addProject(this.state)}>Add</button>
                <button onClick={() => this.props.closeModal()}>close</button>
            </>
        )
    }

}

const mapDispatchToProps = {
    closeModal,
    addProject
}

export default connect(null, mapDispatchToProps)(Modal);
