const rootReducer = (state = {}, action) => {
    switch (action.type) {
        case 'ADD_PROJECT':
            return {
                ...state,
                title: action.payload.data.title
            }
        case 'HANDLE_TOGGLE':
            return {
                ...state,
                open: !state.open
            }
        case 'OPEN_MODAL':
            return {
                ...state,
                modalIsOpen: true
            }
        case 'CLOSE_MODAL':
            return {
                ...state,
                modalIsOpen: false
            }
        //    
        default:
            return state;
    }
}

export default rootReducer;
