import React from 'react';
import styled from 'styled-components';

import lupeIcon from '../../../assets/project-lupe.svg';

const Project = (props) => {

    const Wrapper = styled.div`
        position: relative;
        height: calc(100% - 20px);
        width: calc(100% - 20px);
        transition: 0.3s;
        margin: 10px;
        z-index: 999;
        :hover{
            transition: 0.3s;
            background-color: rgba(46, 204, 113, .7);
            background-image: url(${lupeIcon});
            background-repeat: no-repeat;
            background-position: center;
            cursor: pointer;
        }
    `;

    return(
        <Wrapper>
            <div style={{backgroundImage: 'https://imgcomfort.com/Userfiles/Upload/images/illustration-geiranger.jpg', width: 500, height: 500}}></div>
        </Wrapper>
    )
}

export default Project;
